-- 表的结构 `user_info`
--

CREATE TABLE IF NOT EXISTS `user_info` (
  `user_id` int(11) NOT NULL AUTO_INCREMENT,
  `user_name` varchar(32) COLLATE utf8_bin NOT NULL,
  `user_password` varchar(32) COLLATE utf8_bin NOT NULL,
  `user_email` varchar(32) COLLATE utf8_bin NOT NULL,
  `user_phone` varchar(32) COLLATE utf8_bin NOT NULL,
  `user_area` varchar(32) COLLATE utf8_bin NOT NULL,
  `user_sex` varchar(32) COLLATE utf8_bin NOT NULL,
  `user_hoby` varchar(32) COLLATE utf8_bin NOT NULL,
  `user_date` varchar(32) COLLATE utf8_bin NOT NULL,
  PRIMARY KEY (`user_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COLLATE=utf8_bin AUTO_INCREMENT=3 ;

